import Foundation
import SwiftUI

struct Articulo: Identifiable {
    var title: String
    var author: String
    var authorPicture: Image
    var previewImage: Image
    var date: Date
    let URL: String
    
    var textBody: [String]
    var imageBody: [Image]
    
    var id: String { title }
    
    init(title: String, author: String?, authorPicture: Image?, previewImage: Image?, dateComp: [Int]?, textBody: [String], imageBody: [Image], URL: String) {
        self.title = title
        self.author = author ?? "Anonimo"
        self.authorPicture = authorPicture ?? Image(systemName: "person.slash.fill")
        self.previewImage = previewImage ?? Image(systemName: "photo.artframe")
        self.URL = URL
        
        if dateComp != nil {
            let dateComponents = DateComponents(year: dateComp![0], month: dateComp![1], day: dateComp![2])
            let cal = Calendar(identifier: .gregorian)
            date = cal.date(from: dateComponents)!
        } else { date = Date() }
        
        self.textBody = textBody
        self.imageBody = imageBody
    }
}

